import React, { useState } from 'react';
import './ContactUs.css';

function ContactUs() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission logic here
    console.log('Name:', name);
    console.log('Email:', email);
    console.log('Message:', message);
    // Reset form fields
    setName('');
    setEmail('');
    setMessage('');
  };

  return (
    <div id="contact-us" className="contact-us-container">
      <h1>Contact Us</h1>
      <form className="contact-form" onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={handleNameChange}
          required
        />

        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={handleEmailChange}
          required
        />

        <label htmlFor="message">Message:</label>
        <textarea
          id="message"
          value={message}
          onChange={handleMessageChange}
          required
        />

        <button type="submit">Submit</button>
      </form>

      <div className="faq-container">
        <h2>FAQ</h2>
        <div className="accordion">
          <div className="accordion-item">
            <input type="checkbox" id="faq1" className="accordion-item-toggle" />
            <label htmlFor="faq1" className="accordion-item-title">
              What is your refund policy?
            </label>
            <div className="accordion-item-content">
              Our refund policy allows for a full refund within 30 days of purchase. Please contact our support team for assistance.
            </div>
          </div>

          <div className="accordion-item">
            <input type="checkbox" id="faq2" className="accordion-item-toggle" />
            <label htmlFor="faq2" className="accordion-item-title">
              How can I change my account password?
            </label>
            <div className="accordion-item-content">
              To change your account password, go to the account settings page and follow the instructions provided.
            </div>
          </div>

          <div className="accordion-item">
            <input type="checkbox" id="faq3" className="accordion-item-toggle" />
            <label htmlFor="faq3" className="accordion-item-title">
              Can I upgrade my subscription plan?
            </label>
            <div className="accordion-item-content">
              Yes, you can upgrade your subscription plan at any time. Simply go to the subscription page and choose the desired plan.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ContactUs;
