import React, { useState } from 'react';
import './CardList.css';
import CardItem from './CardItem';

function CardList() {
  const [filters, setFilters] = useState([]);

  const handleFilterChange = (filter) => {
    if (filters.includes(filter)) {
      setFilters(filters.filter((f) => f !== filter));
    } else {
      setFilters([...filters, filter]);
    }
  };

  const cardItems = [
    {
      src: 'images/img-1.jpg',
      text: 'OpenAI CEO Sam Altman Says AI Is ‘Most Important Step Yet’ For Humans and Tech',
      url: 'https://www.bloomberg.com/news/articles/2023-06-22/chatgpt-maker-openai-ceo-speaks-at-bloomberg-tech-summit',
      type: 'text',
      price: 'free'
    },
    {
      src: 'images/img-2.jpg',
      text: 'AI Teachable Moment: How ChatGPT Is Transforming the Classroom',
      url: 'https://www.cnet.com/tech/features/ais-teachable-moment-how-chatgpt-is-transforming-the-classroom/',
      type: 'news',
      price: 'paid'
    },
    {
      src: 'images/img-3.jpg',
      text: 'I use Google Bard as my intern. Here are 5 prompts I use to get the most help from my chatbot.',
      url: 'https://www.businessinsider.com/how-i-use-google-bard-as-a-writer-prompt-chatbots-2023-6',
      type: 'news',
      price: 'free'
    },
    {
      src: 'images/img-4.jpg',
      text: 'The ULTIMATE Advanced Guide to Prompt Engineering with GPT-4 | AI Core Skills',
      url: 'https://www.youtube.com/watch?v=-XivIt_5oSw&ab_channel=LiamOttley',
      type: 'tutorials',
      price: 'free'
    },
    {
      src: 'images/img-5.jpg',
      text: 'ChatGPT Prompt Engineering Course',
      url: 'https://www.youtube.com/watch?v=mBYu5NoXBcs&ab_channel=H-EDUCATE',
      type: 'tutorials',
      price: 'free'
    },
    {
      src: 'images/img-6.jpg',
      text: 'The Beginners Guide to Prompt Engineering: How to Unlock LLMs like ChatGPTs Full Potential!',
      url: 'https://www.youtube.com/watch?v=A806OYAveYA&ab_channel=ZeroToMastery',
      type: 'tutorials',
      price: 'paid'
    },
    // Add more card items here
    // ...
  ];

  const filteredItems = cardItems.filter((item) => {
    if (filters.length === 0) {
      return true;
    }

    const hasTextFilter = filters.includes('text');
    const hasImageFilter = filters.includes('image');
    const hasFreeFilter = filters.includes('free');
    const hasPaidFilter = filters.includes('paid');

    if (hasTextFilter && hasFreeFilter && item.type === 'text' && item.price === 'free') {
      return true;
    }

    if (hasTextFilter && hasPaidFilter && item.type === 'text' && item.price === 'paid') {
      return true;
    }

    if (hasImageFilter && hasFreeFilter && item.type === 'image' && item.price === 'free') {
      return true;
    }

    if (hasImageFilter && hasPaidFilter && item.type === 'image' && item.price === 'paid') {
      return true;
    }

    if (hasTextFilter && hasImageFilter && (item.type === 'text' || item.type === 'image')) {
      return true;
    }

    if (hasFreeFilter && hasPaidFilter) {
      return true;
    }

    return false;
  });

  return (
    <div className='card-list'>
      <h1>Card List</h1>
      <div className='filter-buttons'>
        <label>
          <input
            type='checkbox'
            checked={filters.includes('text')}
            onChange={() => handleFilterChange('text')}
          />
          Text
        </label>
        <label>
          <input
            type='checkbox'
            checked={filters.includes('image')}
            onChange={() => handleFilterChange('image')}
          />
          Image
        </label>
        <label>
          <input
            type='checkbox'
            checked={filters.includes('free')}
            onChange={() => handleFilterChange('free')}
          />
          Free
        </label>
        <label>
          <input
            type='checkbox'
            checked={filters.includes('paid')}
            onChange={() => handleFilterChange('paid')}
          />
          Paid
        </label>
      </div>
      <div className='card-grid'>
        {filteredItems.map((card, index) => (
          <CardItem key={index} src={card.src} text={card.text} url={card.url} />
        ))}
      </div>
    </div>
  );
}

export default CardList;
